const engine = require("./index.js");
const app = require("express")();
engine(app, {exclusivePrefix: "Tests."});
app.set('views', '.')
app.set('view engine', 'jsembeds');

app.get("/", function(req, res) {
	res.render("testSuccess", { value: "Value test: Passed" });
});

app.listen(3000);